actions.script:
function getBrowserInfo() {
    var userAgent = navigator.userAgent;
    var browserName, browserVersion;

    if (userAgent.indexOf("Chrome") > -1) {
        browserName = "Chrome";
        browserVersion = userAgent.split("Chrome/")[1].split(" ")[0];
    } else if (userAgent.indexOf("Firefox") > -1) {
        browserName = "Firefox";
        browserVersion = userAgent.split("Firefox/")[1];
    } else if (userAgent.indexOf("Safari") > -1) {
        browserName = "Safari";
        browserVersion = userAgent.split("Version/")[1].split(" ")[0];
    } else {
        browserName = "Unknown";
        browserVersion = "Unknown";
    }

    return browserName + " " + browserVersion;
}

function getOSInfo() {
    var userAgent = navigator.userAgent;
    var osName = "Unknown OS";
    var osVersion = "Unknown Version";

    if (userAgent.indexOf("Windows NT 10.0") > -1) {
        osName = "Windows";
        osVersion = "10";
    } else if (userAgent.indexOf("Windows NT 6.3") > -1) {
        osName = "Windows";
        osVersion = "8.1";
    } else if (userAgent.indexOf("Windows NT 6.2") > -1) {
        osName = "Windows";
        osVersion = "8";
    } else if (userAgent.indexOf("Windows NT 6.1") > -1) {
        osName = "Windows";
        osVersion = "7";
    } else if (userAgent.indexOf("Windows NT 6.0") > -1) {
        osName = "Windows";
        osVersion = "Vista";
    } else if (userAgent.indexOf("Windows NT 5.1") > -1) {
        osName = "Windows";
        osVersion = "XP";
    } else if (userAgent.indexOf("Macintosh") > -1) {
        osName = "Mac OS";
        osVersion = userAgent.split("Mac OS X ")[1] ? userAgent.split("Mac OS X ")[1].split(")")[0] : "Unknown";
    } else if (userAgent.indexOf("Android") > -1) {
        osName = "Android";
        osVersion = userAgent.split("Android ")[1].split(" ")[0];
    } else if (userAgent.indexOf("Linux") > -1) {
        osName = "Linux";
    }

    return osName + " " + osVersion;
}

function getDeviceType() {
    return /Mobi|Android/i.test(navigator.userAgent) ? "Mobile" : "Desktop";
}

var currentTime = new Date().toLocaleString();
var browserInfo = getBrowserInfo();
var osInfo = getOSInfo();
var screenResolution = screen.width + "x" + screen.height;
var browserLanguage = navigator.language || navigator.userLanguage;
var deviceModel = navigator.platform;
var networkType = (navigator.connection || {}).effectiveType || "Unknown";
var timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;
var connectionSpeed = (navigator.connection || {}).downlink || "Unknown";
var userAgentString = navigator.userAgent;
var cookiesEnabled = navigator.cookieEnabled ? "Yes" : "No";
var javascriptEnabled = "Yes";
var screenOrientation = screen.orientation ? screen.orientation.type : "Unknown";
var deviceMemory = (navigator.deviceMemory || "Unknown") + " GB";
var viewportSize = window.innerWidth + "x" + window.innerHeight;
var cpuCores = navigator.hardwareConcurrency || "Unknown";
var deviceType = getDeviceType();

runtime.globalVars.currentTime = currentTime;
runtime.globalVars.browserInfo = browserInfo;
runtime.globalVars.osInfo = osInfo;
runtime.globalVars.screenResolution = screenResolution;
runtime.globalVars.browserLanguage = browserLanguage;
runtime.globalVars.deviceModel = deviceModel;
runtime.globalVars.networkType = networkType;
runtime.globalVars.timezone = timezone;
runtime.globalVars.connectionSpeed = connectionSpeed;
runtime.globalVars.userAgentString = userAgentString;
runtime.globalVars.cookiesEnabled = cookiesEnabled;
runtime.globalVars.javascriptEnabled = javascriptEnabled;
runtime.globalVars.screenOrientation = screenOrientation;
runtime.globalVars.deviceMemory = deviceMemory;
runtime.globalVars.viewportSize = viewportSize;
runtime.globalVars.cpuCores = cpuCores;
runtime.globalVars.deviceType = deviceType;

var gameVersion = runtime.projectVersion; // This will automatically take the project version

var storedData = localStorage.getItem("yourDataKey") || "No data";
runtime.globalVars.storedData = storedData;

function getClientID() {
    var clientID = localStorage.getItem("clientID");

    // If no ID exists, generate a new one and store it in localStorage
    if (!clientID) {
        clientID = Math.random().toString(36).substr(2, 9); // Generate a unique ID without prefix
        localStorage.setItem("clientID", clientID);
    }

    return clientID;
}
var clientID = getClientID(); // Get or create the unique client ID
runtime.globalVars.clientID = clientID; // Store the client ID in a global variable
